Ext.define('Case.controller.navigation', {
    extend: 'Ext.app.Controller',
    alias: 'controller.navigation',

    requires: [
        'Ext.route.Route'
    ],

    routes: {
        'category': {
            name: 'category',
            action: 'category'
        },
        'catalogs': {
            name: 'catalogs',
            action: 'showtree'
        }
    },

    refs: {
        mainView: 'mainview'
    },

    category: function() {
        let workspace = this.getWorkspace();


        workspace.add({
            xtype: 'mytree'
        });

    },

    getWorkspace: function() {
        return this.getMainView().getReferences().workspace;
    },

    showtree: function() {
        let workspace = this.getWorkspace();
        let container = Ext.create({
            xtype: 'panel',
            title:"Katalog tovarov",
            header:{
              hidden:true
            },
            layout: 'hbox',







        });

        container.add({
            xtype: 'categorytree',
            flex:1

        });
        container.add({
            xtype: 'productgrid',
            flex:4

        });

         workspace.push(container);

    }

});