Ext.define('Case.view.DialogAddProductViewController3', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.dialogaddproduct',

    onButtonTapProduct: function(button, e, eOpts) {
        var me =  this,
            refs = me.getReferences();
        var crecord = button.up('formpanel').getViewModel().get('crecord');


        if (refs.form.validate()) {

            let store = Case.app.getStore('productstore');

            Ext.Ajax.request({
                url: 'productlist/add',
                params:{category_id:crecord.category_id,
                    postavshik_id:crecord.postavshik_id,
                    name:crecord.name,
                    price:crecord.price,
                    quantity:crecord.quantity,
                    opisanie:crecord.opisanie
                },
                success: function(response, opts) {

                    store.load();
                    Ext.Msg.alert("Masege" , "Dannie uspeshno soxraneni");

                },

                failure: function(response, opts) {
                    Ext.Msg.alert("Ошибка" , "Poprobuyte esho raz");
                }
            });
        }

    },

    onDialogBeforeShow: function(component, eOpts) {
        let me = this,
        refs = me.getReferences(),
        vm = me.getViewModel();

        refs.form.validate();
    }

});